// –ó–∞–ø–æ–ª–Ω—è–µ–º –≤—Å–µ –¥–Ω–∏, –µ—Å–ª–∏ –Ω—É–∂–Ω–æ
const monthsDays = {
  "01": 31,
  "02": 28,
  "03": 31,
  "04": 30,
  "05": 31,
  "06": 30,
  "07": 31,
  "08": 31,
  "09": 30,
  "10": 31,
  "11": 30,
  "12": 31
};
for (const month in monthsDays) {
  const daysCount = monthsDays[month];
  for (let d = 1; d <= daysCount; d++) {
    const dayStr = d.toString().padStart(2, '0');
    const dateKey = `${dayStr}.${month}`;
    if (!daysData[dateKey]) {
      daysData[dateKey] = "–û–±—ã—á–Ω—ã–π –¥–µ–Ω—å";
    }
  }
}

// –ü–æ–ª—É—á–∞–µ–º —Ç–µ–∫—É—â–∏–π –¥–µ–Ω—å
const today = new Date();
const startOfYear = new Date(today.getFullYear(), 0, 0);
const diffMs = today - startOfYear;
const oneDayMs = 1000 * 60 * 60 * 24;
let dayOfYear = Math.floor(diffMs / oneDayMs);
if (dayOfYear > 365) dayOfYear = 365; // –ø–æ –Ω–µ–≤–∏—Å–æ–∫–æ—Å–Ω–æ–º—É –≥–æ–¥—É

// –§–æ—Ä–º–∞—Ç–∏—Ä—É–µ–º –¥–∞—Ç—É
const day = today.getDate().toString().padStart(2, '0');
const month = (today.getMonth() + 1).toString().padStart(2, '0');
const todayKey = `${day}.${month}`;

// –ü–æ–ª—É—á–∞–µ–º –æ–ø–∏—Å–∞–Ω–∏–µ
let description = daysData[todayKey] || "–≠—Ç–æ—Ç –¥–µ–Ω—å –Ω–µ –Ω–∞–π–¥–µ–Ω –≤ –¥–∞–Ω–Ω—ã—Ö";

// –í—Å—Ç–∞–≤–ª—è–µ–º –æ–ø–∏—Å–∞–Ω–∏–µ –≤ –±–ª–æ–∫ –∏ —Å–æ—Ö—Ä–∞–Ω—è–µ–º —Ç–µ–∫—É—â–∏–π —Ä–∞–∑–º–µ—Ä —à—Ä–∏—Ñ—Ç–∞
const descriptionDiv = document.getElementById('todayDescription');
descriptionDiv.innerHTML = description;

// –†–µ–≥—É–ª—è—Ç–æ—Ä —Ä–∞–∑–º–µ—Ä–∞ —à—Ä–∏—Ñ—Ç–∞
const fontSizeRange = document.getElementById('fontSizeRange');
const fontSizeValue = document.getElementById('fontSizeValue');

// –û–±—Ä–∞–±–æ—Ç—á–∏–∫ –∏–∑–º–µ–Ω–µ–Ω–∏—è –ø–æ–ª–∑—É–Ω–∫–∞
fontSizeRange.addEventListener('input', () => {
  const size = fontSizeRange.value;
  descriptionDiv.style.fontSize = size + 'px';
  fontSizeValue.textContent = size + 'px';
});
